import { Component } from '@angular/core';
import { ConsultaService } from 'src/app/services/consulta.service';
import { Libro } from 'src/app/models/libro';
import { libroService } from 'src/app/services/libro.service';
import { EditorialService } from 'src/app/services/editorial.service';
import { OnInit } from '@angular/core';



@Component({
  selector: 'app-consultar-libros',
  templateUrl: './consultar-libros.component.html',
  styleUrls: ['./consultar-libros.component.css']
})
export class ConsultarLibrosComponent implements OnInit{

  searchTitle: string = '';
  searchAuthor: string = '';
  searchClassification: string = 'Clasificacion';

  librosFiltrados: Libro[] = [];

  constructor(public libroService:libroService, public editorialService:EditorialService) { }

  ngOnInit(): void {
    //console.log(this.empleadoService.getEmpleados());
    this.getLibros();
    this.getEditorial();
  }

  performSearch(){
    this.librosFiltrados = this.libroService.libros.filter(libro => {
      const coincideTitulo = libro.titulo.toLowerCase().includes(this.searchTitle.toLowerCase());
      const coincideAutor = libro.autores.toLowerCase().includes(this.searchAuthor.toLowerCase());
      const coincideClasificacion = this.searchClassification === 'Clasificacion' || libro.clasificacion === this.searchClassification;

      return coincideTitulo && coincideAutor && coincideClasificacion;
    })
  }

  getEditorial(){
    this.editorialService.getEditorial().subscribe(
      res => {
        this.editorialService.editoriales= res;
        console.log(res);
      },
      err => console.log(err)
    )
  }

  getLibros(){
    this.libroService.getLibro().subscribe(
      res => {
        this.libroService.libros= res;
        console.log(res);
      },
      err => console.log(err)
    )
  }






  createPrestamo(_id:any){
    alert('Reservado!!');
     const resp= confirm('Estas seguro de reservarlo?');
     console.log('reservado '+_id);
     if(resp){
       this.libroService.cratePrestamo(_id).subscribe(
        res=>{
          this.getLibros();
        },
        (err)=> console.log(err)
       );
     }
  }

}
